mapper.select:
SELECT count(*)
	FROM rh_user
		WHERE user_id = #{user_id}

 SELECT count(*)
 	FROM rh_user
		 WHERE nickname = #{nickname}

SELECT user_pw
	FROM rh_user
		WHERE user_id = #{user_id} 
			  AND pw_quest = #{pw_quest} 
			  AND pw_quest_answer = #{pw_quest_answer}

mapper.update:
UPDATE rh_user 
	SET user_pw = #{new_pw}
		WHERE user_id = #{user_id}
